import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

export default function App() {
  const [friends, setFriends] = useState(initialFriends);
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [selectedFriend, setSelectedFriend] = useState(null);

  function handleSelected(friend) {
    setSelectedFriend((cur) => (cur?.id === friend.id ? null : friend));
    setShowAddFriend(false);
  }
  function handleShowAddFriend() {
    setShowAddFriend((show) => !show);
  }
  function handleAddFriends(friend) {
    setFriends((friends) => [...friends, friend]);
    setShowAddFriend(false);
  } //always create a new arrary

  function handleSpiltBill(value) {
    setFriends(friends=> friends.map(friend=> friend.id===selectedFriend.id ? {...friend, balance: friend.balance+value}:friend))
setSelectedFriend(n)
  }

  return (
    <div className="app">
      <div className="sidebar">
        <FriendList
          selectedFriend={selectedFriend}
          friends={friends}
          onSelection={handleSelected}
        />
        {/* ÂΩìadd a new friend,the state friends change, thus re-render */}
        {showAddFriend && <FormAddFriend onAddFriend={handleAddFriends} />}
        <Button onClick={handleShowAddFriend}>
          {showAddFriend ? "Close" : "Add friends"}
        </Button>
      </div>
      {selectedFriend && <FormSplitBill selectedFriend={selectedFriend} onSplit={handleSpiltBill}/>}
    </div>
  );
}

function FriendList({ selectedFriend, friends, onSelection }) {
  return (
    <ul>
      {friends.map((f) => (
        <EachFriend
          selectedFriend={selectedFriend}
          friend={f}
          key={f.id}
          onSelection={onSelection}
        />
      ))}
    </ul>
  );
}

function EachFriend({ friend, onSelection, selectedFriend }) {
  const isSelect = friend.id === selectedFriend?.id;
  return (
    <li className={isSelect ? "selected" : ""}>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>
      {friend.balance < 0 && (
        <p className="red">
          You Owe {friend.name} {Math.abs(friend.balance)}{" "}
        </p>
      )}
      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owe you {Math.abs(friend.balance)}{" "}
        </p>
      )}
      {friend.balance === 0 && <p>You and {friend.name} are even </p>}
      <Button onClick={() => onSelection(friend)}>
        {isSelect ? "Close" : "Select"}
      </Button>
    </li>
  );
}

function FormAddFriend({ onAddFriend }) {
  const [name, setName] = useState("");
  const [imgUrl, setImgUrl] = useState("https://i.pravatar.cc/48");

  function handleSumbit(e) {
    e.preventDefault();
    const id = crypto.randomUUID();

    if (!name || !imgUrl) return;
    const newFriend = {
      id,
      name,
      image: `${imgUrl}?=${id}`,
      balance: 0,
    };
    onAddFriend(newFriend);
    setName("");
    setImgUrl("https://i.pravatar.cc/48");
  }

  return (
    <form className="form-add-friend" onSubmit={handleSumbit}>
      <label> üòß Name</label>
      <input
        type="text"
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
      />

      <label> üå≤ URL</label>
      <input
        type="text"
        value={imgUrl}
        onChange={(e) => {
          setImgUrl(e.target.value);
        }}
      />

      <Button>Add</Button>
    </form>
  );
}

function FormSplitBill({ selectedFriend,onSplit }) {
  const [bill, setBill] = useState("");
  const [paidByUser, setPaidByUser] = useState("");
  const paidByFriend = paidByUser ? bill - paidByUser : "";
  const [whoPay, setWhoPay] = useState("user");
  function handleSumbit(e) {
    e.preventDefault();
    if (!bill || !paidByUser) return;
    onSplit(whoPay === 'user' ? paidByFriend : -paidByUser )
  }
  return (
    <form className="form-split-bill" onSubmit={handleSumbit}>
      <h2>SPLIT A BILL WITH {selectedFriend.name}</h2>

      <label>üí∞ Bill Value</label>
      <input
        type="text"
        value={bill}
        onChange={(e) => setBill(Number(e.target.value))}
      />

      <label>üë∏ Your expense</label>
      <input
        type="text"
        value={paidByUser}
        onChange={(e) =>
          setPaidByUser(
            Number(e.target.value) > bill ? paidByUser : Number(e.target.value)
          )
        }
      />

      <label>üßë‚Äçü§ù‚Äçüßë {selectedFriend.name}'s expense</label>
      <input type="text" disabled value={paidByFriend} />
      <label>üòß Who is paying the bill</label>
      <select value={whoPay} onChange={(e) => setWhoPay(e.target.value)}>
        <option value="user">You</option>
        <option value="friend">{selectedFriend.name}</option>
      </select>
      <Button>Split Bill</Button>
    </form>
  );
}
